import React , {useState} from 'react';
import {ProgressBar} from '@react-native-community/progress-bar-android';

import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  TextInput,
  Share,
} from 'react-native';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const getDataUsingGet = () => {
    fetch(
      'https://tinyurl.com/api-create.php?url='+ inputValue.toString(),
      {
        method: 'GET',
      },
    )
      .then((response) => response.text())
      .then((responseJson) => {
        console.log(responseJson);
        Share.share({
          message:
            'Halo Kamu Telah Menerima Link Yang Telah Di Bagikan Lewat Aplikasi Simplify! Berikut Ini Adalah Linknya ' + responseJson + ' Terima Kasih!',
        })
          .then((result) => console.log(result))
          .catch((errorMsg) => console.log(errorMsg));
      })
      .catch((error) => {
        alert('Error -> ' + JSON.stringify(error));
        console.error(error);
      });
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.container}>
        <Text style={styles.titleText}>
          Link Shortener Menggunakan (TinyURL) 
        </Text>
        <Text style={styles.textStyle}>
          Masukkan Link Yang Ingin Di - Simplify
        </Text>
        <TextInput  
          value={inputValue}
          onChangeText={
            (inputValue) => setInputValue(inputValue)
          }
          placeholder={'Enter Text to Share'}
          style={styles.textInput}
        />
        <TouchableOpacity
          activeOpacity={0.7}
          style={styles.buttonStyle}
          onPress={getDataUsingGet}>
          <Text style={styles.buttonTextStyle}>
            Bagikan Link Yang Di - Simplify
          </Text>
        </TouchableOpacity>

        {/* <View style={styles.example}>
        <Text>Horizontal Progress Indicator</Text>
        <ProgressBar styleAttr="Horizontal" />
       </View> */}

        <Text style={styles.textStyle}>
          Loading Bar
        </Text>
      </View>
    </SafeAreaView>
  );
};

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    padding: 10,
    justifyContent: 'center',
    textAlign: 'center',
  },
  titleText: {
    padding: 8,
    fontSize: 22,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  textStyle: {
    textAlign: 'center',
    fontSize: 18,
    color: '#000',
    marginTop: 15,
  },
  textStyleSmall: {
    textAlign: 'center',
    padding: 5,
  },
  buttonStyle: {
    justifyContent: 'center',
    flexDirection: 'row',
    marginTop: 30,
    padding: 15,
    backgroundColor: '#8ad24e',
  },
  buttonTextStyle: {
    color: '#fff',
    textAlign: 'center',
  },
  textInput: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    width: '100%',
    paddingHorizontal: 10,
  },
});
